  font-size: 1.3rem;
  text-align: center;
}
.footer__nav {
  justify-content: space-between;
  display: flex;
  align-items: center;
}
@media only screen and (max-width: 31.25em) {
  .footer__nav {
    flex-direction: column;
  }
}
.footer .nav__link {
  color: #f4f4f4;
  background-image: linear-gradient(90deg, rgba(0, 0, 0, 0) 0 50%, #000000 50%);
}
.footer hr {
  width: 72%;
  margin: 0 auto;
  color: rgba(0, 0, 0, 0.3098039216);
}

