        width: 48%;
        text-align: center;
        font-size: 1.35rem;
        line-height: 1.7;
        font-weight: 600;
        color: #fff;
        

        @media only screen and (max-width: $bp-medium) { 
            width: 90%;
            font-size: 1.5rem;
        }
        
        @media only screen and (max-width: $bp-smallest) { 
            width: 100%;
            text-align: justify;
            font-size: 1.3rem;
        }
    }


}

sources.css\\_members.scss:
.members {
    grid-column: full-start / center-end;
    margin: 15rem 0;


    &__header {
        display: flex;
        align-items: center;
        justify-content: space-between;
    }

    &__group {
        overflow-x: scroll;
        scroll-behavior: smooth;
        display: flex;
        column-gap: 3.5rem;

        &::-webkit-scrollbar{
            display: none;
        }

        scrollbar-width: none !important;
    }

    &__box{

        &__desc {
            font-size: 1.35rem;
            margin-top: .5rem;
        }
        &__img{
            height: 30rem;
            width: 22rem;
            object-fit: cover;
            object-position: center;
            margin-bottom: 1rem;
            box-shadow: 4px 6px 10px 0px rgba(0,0,0,0.48);
    
            img {
                width: 100%;
                height: 100%;
            }
    
        }
    }
    
}

.arrows{
    display: flex;
    gap: 1rem;
}



.next,.prev {
    width: 4rem;
    height: 4rem;
    border: 2px solid #0297D3;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    @media only screen and (max-width: $bp-smallest) { 
        visibility: hidden;
    }

    img {
        height: 2rem;
        width: 2rem;
    }
}

// .next {
//     background-color: #fff;
// }

// .prev {
//     background-color: #000
// }
.visible {
    visibility: visible !important;
}
.modal {
    height: 100%;
    width: 100%;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    background-color:rgba(0,0,0,0.8);
    position: fixed;
    z-index: 3000;
    margin: 0 auto;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
    gap: 2rem;
    overflow-x: hidden;
    overflow-y: hidden;
    backdrop-filter: blur(6px);
    visibility: hidden;
    transition: all 0.5s;
    @media only screen and (max-width: $bp-smallest) { 
        padding: 0 2rem;
    }

    &__paragraph {
        width: 57%;
        color: #fff;
        text-align: center;
        z-index: 3500;
        font-size: 1.8rem;
        font-weight: 600;
        line-height: 1.7;
        position: relative;
        overflow: hidden;

        @media only screen and (max-width: $bp-smallest) { 
            width: 100%;
            text-align: justify;
            font-size: 1.5rem;
        }
    }

    &__image {
        height: 9rem;
        width: 9rem;
        object-fit: fill;
        border-radius: 100%;
    }

    &__buttons{
        display: flex;
        gap: 1rem;

        @media only screen and (max-width: $bp-smallest) { 
            flex-direction: column;
            align-items: center;
        }
        
    }
}

sources.css\\_what_we_do.scss:
.what_we_do { 
    grid-column: full-start / center-end;
    display: grid;
    grid-template-columns: repeat(2,minmax(min-content, 1fr));
    margin: 15rem 0;

    @media only screen and (max-width: $bp-medium) {
        grid-template-rows: repeat(2,min-content);
        grid-template-columns: none;
        gap: 5rem 0;
     }
}


.boxes {
    display: grid;
    grid-template-columns: repeat(4, calc(25% - 3.5rem));
    grid-column-gap: 3.5rem;
    margin-right: -3.5rem;

    @media only screen and (max-width: $bp-medium) {
        grid-template-columns: none;
        grid-row-gap: 1.5rem;
        grid-template-rows: repeat(4, min-content);
        margin-right: 0;
        display: grid;
    }
}

.box {
    background: black;
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 2rem 0;
    height: 50rem;
    opacity: 0;
    transition: all 1s;

    @media(prefers-reduced-motion) {
        transition: none;
    }

 
    @media only screen and (max-width: $bp-medium) {
        height: 15rem;
        background: #000;
        flex-direction: row;
        align-items: center;
        padding: 2rem 0;
        display: flex;
        column-gap: 2rem;
     }
    
    img{
        height: 5rem;
        width: 5rem;
        margin-bottom: 6rem;

        @media only screen and (max-width: $bp-medium) {
            margin-bottom: 0;
            margin-left: 6rem;
        }
    }

    &__line{
        height: 0.2rem;
        width: 9rem;
        rotate: 90deg;
        background: #707070;
        @media only screen and (max-width: $bp-medium) {
            width: 15rem;
            rotate: initial;
         }

    }

    &__text {
        color: white;
        font-size: 1.5rem;
        text-transform: uppercase;
        font-weight: 600;
        margin-top: 6rem;
        writing-mode: tb;
        letter-spacing: 1.5px;

        @media only screen and (max-width: $bp-medium) {
            writing-mode: horizontal-tb;
            margin-top: 0;
        }
    }

    //Animation
    &:nth-child(2) {
        transition-delay: 200ms;
    }
    &:nth-child(3) {
        transition-delay: 400ms;
    }
    &:nth-child(4) {
        transition-delay: 600ms;
    }
}

.text-box{
    &__paragraph {
        width: 84%;
        font-size: 1.5rem;
        color: $color-black;
        font-weight: 600;
        line-height: 1.5;
        

        @media only screen and (max-width: $bp-medium) {
            font-size: 1.8rem;
            width: 100%;
        }
    }

}

sources.css\\_join.scss:
.join {
    grid-column: full-start / center-end;
    margin: 15rem 0;
    display: grid;
    grid-template-columns: repeat(2, minmax(min-content, 1fr));
    align-items: center;

    &__buttons {
        display: flex;
        column-gap: 2rem;
        align-items: center;
        flex-wrap: wrap;
        @media only screen and (max-width: $bp-medium) { 
            gap: 2rem;
        }
    }
    @media only screen and (max-width: $bp-medium) { 
        gap: 5rem;
        grid-template-columns: none;
        grid-template-rows: repeat(2, min-content);
    }
    &__img{
        width: 90%;
    }
  }

sources.css\\_gallery.scss:
.gallery{
    grid-column: sidebar-start / span 11;
    margin: 15rem 0;

    &__groups {
        display: grid;
        grid-template-columns: repeat(6, 1fr);
        grid-template-rows: repeat(12, 10vw);
        grid-gap: 2rem;

        @media only screen and (max-width: $bp-medium) { 
            grid-gap: .5rem;
        }

        @media only screen and (max-width: $bp-smallest) { 
            grid-gap: 0rem;
        }

        
    }
    &-res{
        @media only screen and (max-width: $bp-smallest) { 
            margin-left: 3rem !important;
        }
    }

    &__img {
        height: 100%;
        width: 100%;
        -o-object-fit: cover;
        object-fit: cover;
        display: block;
    }
    
    &__item {
        opacity: 0;
        transition: all 2s;

        @media(prefers-reduced-motion) {
            transition: none;
        }

        &--1{
            grid-column: 1/span 2;
            grid-row: 1/span 2;
            
        }

        &--2{
            grid-area: 1 / 3 / span 5 / span 2;
            transition-delay: 200ms;
        }

        &--3{ 
            grid-area: 1 / 5 / span 3 / span 2;
            transition-delay: 300ms;
        }

        &--4{
            grid-area: 3 / 1 / span 4 / span 2;
            transition-delay: 400ms;
        }

        &--5{ 
            grid-area: 7 / 1 / span 2 / span 2;
            transition-delay: 500ms;
        }

        &--6{
            grid-area: 6 / 3 / span 3 / span 2;
            transition-delay: 600ms;
        }

        &--7{
            grid-area: 4 / 5 / span 2 / span 2;
            transition-delay: 700ms;
        }

        &--8{
            grid-area: 6 / 5 / span 3 / span 2;
            transition-delay: 800ms;
        }
        &--9{
            grid-area: 9 / 1 / span 3 / span 4;
            transition-delay: 900ms;
        }

        &--10{
            grid-area: 9 / 5 / span 4 / span 2;
            transition-delay: 1000ms;
        }
    }

}

.show{
    opacity: 1;
}

sources.css\\_footer.scss:
.footer {
    background-color: $color-primary;
    grid-column: sidebar-start / span 11;
    padding: 5rem;
    display: grid;
    color: #fff;
    grid-template-columns: [sidebar-start] 8rem[sidebar-end full-start] 
        minmax(6rem, 1fr)[center-start] repeat(8, [col-start] 
        minmax(min-content, 14rem)[col-end])[center-end] 8rem[full-end];

    
        @media only screen and (max-width: $bp-medium) { 
            grid-template-columns: [sidebar-start] 1.5rem[sidebar-end full-start] 
                minmax(6rem, 1fr)[center-start] repeat(8, [col-start] 
                minmax(min-content, 14rem)[col-end])[center-end] 1.5rem[full-end];
        }
    position: relative;

    &__map {
        display: flex;
        align-items: start;
        column-gap: .5rem;
        width: 68%;
        text-align: left;
        font-size: 1.3rem;
        @media only screen and (max-width: $bp-smallest) { 
            width: 100%;
            text-align: center;
        }
    }

    &__logo {
        @media only screen and (max-width: $bp-smallest) { 
            text-align: center;
        }
    }
    &__contents{
        grid-column: full-start / center-end;
        display: flex;
        flex-direction: column;
        gap: 6rem;

        .copyright{
            font-size: 1.3rem;
            text-align: center;
        }
    }

    &__nav {
        justify-content: space-between;
        display: flex;
        align-items: center;

        @media only screen and (max-width: $bp-smallest) { 
            flex-direction: column;
        }
    }

    .nav__link {
        color: #f4f4f4;
        background-image: linear-gradient(90deg, #0000 0 50%, $color-black 50%);
    }

    hr{
        width: 72%;
        margin: 0 auto;
        color: #0000004f;
    }
}

